    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        String userName = "admin";
        String password = "admin";
        
        String currentUsername = txtUsername.getText();
        char[] currentPassword = txtPassword.getPassword();
        String enteredPassword = new String(currentPassword);

         if (userName.equals(currentUsername) && password.equals(enteredPassword)) {
            FrmDPEXSystemMenu frmDPEXSystemMenu = new FrmDPEXSystemMenu();
            this.setVisible(false);
            frmDPEXSystemMenu .setVisible(true);
        } else { 
            JOptionPane.showMessageDialog(rootPane, "Usuario y/o contraseña incorrectos", "ERROR", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnLoginActionPerformed

